mapper.update:
UPDATE MATERIALRESOURCE 
SET prjname= (SELECT prjname FROM projectbasic WHERE prjno=#{prjNo})
WHERE prjno =#{prjNo}

mapper.select:
		SELECT *
     		FROM (
       		select rownum cnt, b.*
        	from MATERIALRESOURCE b
         	where 1=1
         	and materialresourcename LIKE '%'||#{materialresourcename}||'%'
         	and prjNo=#{prjNo}
      		)
     	 	WHERE cnt BETWEEN #{start} AND #{end}

		SELECT sum(price*PRODUCTCNT)
     		FROM (
       		select rownum cnt, b.*
        	from MATERIALRESOURCE b
         	where 1=1
         	and materialresourcename LIKE '%'||#{materialresourcename}||'%'
         	and prjNo=#{prjNo}
      		)
     	 	WHERE cnt BETWEEN #{start} AND #{end}

			SELECT *
     		FROM (
	       		select rownum cnt, b.*
	        	from humanresource b
	         	where 1=1
	         	and tel LIKE '%'||#{tel}||'%'
	         	and prjNo=#{prjNo}
      		)
     	 	WHERE cnt BETWEEN #{start} AND #{end}

			SELECT sum(sal)
     		FROM (
	       		select rownum cnt, b.*
	        	from humanresource b
	         	where 1=1
	         	and tel LIKE '%'||#{tel}||'%'
	         	and prjNo=#{prjNo}
      		)
     	 	WHERE cnt BETWEEN #{start} AND #{end}

mapper.update:
UPDATE HUMANRESOURCE 
SET prjno=#{prjNo},
deptno=#{deptno},
empno=#{empno},
sal=#{sal}
WHERE tel=#{tel}

mapper.select:
SELECT PRODUCTCNT 
FROM materialresource
WHERE materialresourceno=#{materialresourceno}

mapper.update:
UPDATE MATERIALRESOURCE 
SET PRODUCTCNT=PRODUCTCNT - #{cntInput}
WHERE MATERIALRESOURCENO=#{materialresourceno}

UPDATE REPOSITORY 
SET repcnt=repcnt - #{cntInputRepToMr}
WHERE repno=#{repno}

mapper.select:
SELECT repcnt 
FROM REPOSITORY 
WHERE repno=#{repno}
