mapper.update:
update FB_dept
set
dname=#{dname}
where deptno = #{deptno}

mapper.select:
SELECT *
FROM (
select rownum cnt, e.*
from (
SELECT e.*, d.DNAME
from FB_emp e
INNER JOIN fb_dept d ON e.deptno = d.deptno
and ename like
'%'||#{ename}||'%'
order by empno
) e
)
where cnt BETWEEN #{start}
AND#{end}
