mapper.insert:
INSERT INTO riskfile VALUES(
	riskfile_seq.nextval, risk_seq.currval, #{fname}, #{path}, sysdate, sysdate, #{etc}
)

mapper.update:
UPDATE risk
SET riskname=#{riskName}, riskcontent=#{riskContent}, dangerstep=#{dangerStep},
	completedate=TO_DATE(#{completeDateStr}, 'YYYY-MM-DD'), state=#{state}
WHERE riskno=#{riskNo}
